public class SqlGenerator\n{\n    public string GenerateCreateTableStatement(DataTable table, string tableName)\n    {\n        string createTable = $"CREATE TABLE {tableName} (";\n        foreach (DataColumn column in table.Columns)\n        {\n            createTable += $"{column.ColumnName} VARCHAR(255),";\n        }\n        createTable = createTable.TrimEnd(',') + ");";\n        return createTable;\n    }\n\n    public string GenerateInsertStatements(DataTable table, string tableName)\n    {\n        string insertStatements = "";\n        foreach (DataRow row in table.Rows)\n        {\n            string columns = string.Join(",", table.Columns.Cast<DataColumn>().Select(c => c.ColumnName));\n            string values = string.Join(",", row.ItemArray.Select(v => $"'{v.ToString().Replace("'", "''")}'"));\n            insertStatements += $"INSERT INTO {tableName} ({columns}) VALUES ({values});\n";\n        }\n        return insertStatements;\n    }\n}