using System.Configuration;\nusing System.Net.Mail;\n\nnamespace YourNamespace\n{\n    public class EmailService\n    {\n        public void SendEmail(string recipientKey)\n        {\n            var recipientsSection = (RecipientsSection)ConfigurationManager.GetSection("recipientsSection");\n            string recipientEmail = recipientsSection.Recipients[recipientKey]?.Value;\n\n            if (!string.IsNullOrEmpty(recipientEmail))\n            {\n                MailMessage mailMessage = new MailMessage();\n                mailMessage.From = new MailAddress(ConfigurationManager.AppSettings["EmailSender"]);\n                mailMessage.To.Add(new MailAddress(recipientEmail));\n                // Add other mail settings and send the email.\n            }\n            else\n            {\n                // Handle missing recipient case\n            }\n        }\n    }\n}