using System;\nusing System.Windows.Forms;\n\npublic class InstructionsForm : Form\n{\n    private TextBox instructionsTextBox;\n\n    public InstructionsForm()\n    {\n        this.Text = "Instructions";\n        \n        instructionsTextBox = new TextBox();\n        instructionsTextBox.Multiline = true;\n        instructionsTextBox.ReadOnly = true;\n        instructionsTextBox.Dock = DockStyle.Fill;\n        instructionsTextBox.Text = \n            "Welcome to the program!\r\n" +\n            "1. Step one: Do this...\r\n" +\n            "2. Step two: Do that...\r\n" +\n            "3. Step three: Finish up by...\r\n" +\n            "If you need help, refer to the documentation.";\n\n        this.Controls.Add(instructionsTextBox);\n    }\n\n    [STAThread]\n    public static void Main()\n    {\n        Application.EnableVisualStyles();\n        Application.Run(new InstructionsForm());\n    }\n}