using System;\n\npublic class Temperature\n{\n    public double Celsius { get; }\n\n    public Temperature(double celsius)\n    {\n        Celsius = celsius;\n    }\n\n    // Implicit conversion from double to Temperature\n    public static implicit operator Temperature(double celsius)\n    {\n        return new Temperature(celsius);\n    }\n\n    // Explicit conversion from Temperature to double\n    public static explicit operator double(Temperature temp)\n    {\n        return temp.Celsius;\n    }\n\n    public override string ToString()\n    {\n        return $"{Celsius} °C";\n    }\n}\n\npublic class Program\n{\n    public static void Main()\n    {\n        // Using implicit conversion\n        Temperature temp = 36.5;\n        Console.WriteLine(temp); // Output: 36.5 °C\n\n        // Using explicit conversion\n        double celsius = (double)temp;\n        Console.WriteLine(celsius); // Output: 36.5\n    }\n}