using System;\nusing System.Collections.Generic;\nusing SharpSvn;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        string svnUri = "http://svn.example.com/trunk/source"; // Change this to your SVN repository URL\n        List<string> files = new List<string>();\n\n        using (SvnClient client = new SvnClient())\n        {\n            SvnListArgs listArgs = new SvnListArgs();\n            listArgs.Depth = SvnDepth.Infinity;\n\n            client.List(new Uri(svnUri), listArgs, (sender, e) =>\n            {\n                if (e.Entry.NodeKind == SvnNodeKind.File)\n                {\n                    files.Add(e.Entry.Path);\n                }\n            });\n        }\n\n        foreach (string file in files)\n        {\n            Console.WriteLine(file);\n        }\n    }\n}