using System;\nusing System.Xml;\n\nclass Program\n{\n    static void Main()\n    {\n        string fullProjectPath = @"path\to\your\project.csproj"; // Update this with your project file path\n\n        XmlDocument projDefinition = new XmlDocument();\n        projDefinition.Load(fullProjectPath);\n\n        XmlNamespaceManager nsmgr = new XmlNamespaceManager(projDefinition.NameTable);\n        nsmgr.AddNamespace("msb", "http://schemas.microsoft.com/developer/msbuild/2003");\n\n        XmlNodeList referenceNodes = projDefinition.SelectNodes("//msb:ItemGroup/msb:Reference", nsmgr);\n        \n        foreach (XmlNode node in referenceNodes)\n        {\n            Console.WriteLine(node.Attributes["Include"].Value);\n        }\n    }\n}