using System;\nusing System.Windows.Forms;\n\npublic class MyForm : Form\n{\n    private TextBox myTextBox;\n\n    public MyForm()\n    {\n        myTextBox = new TextBox();\n        myTextBox.Location = new System.Drawing.Point(15, 15);\n        myTextBox.TextChanged += new EventHandler(TextBox_TextChanged);\n        \n        this.Controls.Add(myTextBox);\n        this.Load += new EventHandler(Form_Load);\n    }\n    \n    private void TextBox_TextChanged(object sender, EventArgs e)\n    {\n        ValidateTextBox();\n    }\n\n    private void Form_Load(object sender, EventArgs e)\n    {\n        ValidateTextBox();\n    }\n\n    private void ValidateTextBox()\n    {\n        if (string.IsNullOrWhiteSpace(myTextBox.Text))\n        {\n            MessageBox.Show("TextBox cannot be empty or whitespace.");\n        }\n        else\n        {\n            // Additional validation logic\n            Console.WriteLine("TextBox content is valid.");\n        }\n    }\n\n    [STAThread]\n    public static void Main()\n    {\n        Application.EnableVisualStyles();\n        Application.Run(new MyForm());\n    }\n}