// Shared library project\npublic static class DateValidator\n{\n    public static bool ValidateDate(DateTime date)\n    {\n        // Example rule: Date should not be in the past\n        return date >= DateTime.Now.Date;\n    }\n}\n\n// Server-side code\npublic class MyController : ControllerBase\n{\n    [HttpPost]\n    public IActionResult Validate([FromBody] DateTime date)\n    {\n        if (DateValidator.ValidateDate(date))\n        {\n            return Ok();\n        }\n        return BadRequest("Invalid date");\n    }\n}\n\n// Client-side code (Blazor WebAssembly)\n@page "/date-validation"\n\n@code {\n    private DateTime inputDate;\n    private string validationMessage;\n\n    private void ValidateDate()\n    {\n        if (DateValidator.ValidateDate(inputDate))\n        {\n            validationMessage = "Valid date";\n        }\n        else\n        {\n            validationMessage = "Invalid date";\n        }\n    }\n}