// Convert Guid to two 64-bit integers\nGuid guid = Guid.NewGuid();\nbyte[] guidBytes = guid.ToByteArray();\nlong mostSignificantBits = BitConverter.ToInt64(guidBytes, 0);\nlong leastSignificantBits = BitConverter.ToInt64(guidBytes, 8);\n\n// Convert two 64-bit integers back to Guid\nbyte[] combinedBytes = new byte[16];\nArray.Copy(BitConverter.GetBytes(mostSignificantBits), 0, combinedBytes, 0, 8);\nArray.Copy(BitConverter.GetBytes(leastSignificantBits), 0, combinedBytes, 8, 8);\nGuid newGuid = new Guid(combinedBytes);\n\n// Output for verification\nConsole.WriteLine($"Original Guid: {guid}");\nConsole.WriteLine($"New Guid from 64-bit integers: {newGuid}");