using Oracle.DataAccess.Client; // Make sure you have the correct namespace\n\nstring connectionString = "Your Connection String Here";\nusing (OracleConnection connection = new OracleConnection(connectionString))\n{\n    connection.Open();\n    using (OracleCommand command = new OracleCommand("SELECT FLOOR(TO_NUMBER(your_column)) AS integer_value FROM your_table", connection))\n    {\n        using (OracleDataReader reader = command.ExecuteReader())\n        {\n            while (reader.Read())\n            {\n                int integerValue = reader.GetInt32(reader.GetOrdinal("integer_value"));\n                // Use integerValue as needed\n            }\n        }\n    }\n}