// Example of a simple Entity Framework model class\npublic class Product\n{\n    public int ProductId { get; set; }\n    public string Name { get; set; }\n    public decimal Price { get; set; }\n}\n\n// Example of a simple Telerik Data Access model class\n[Persistent]\npublic class Product\n{\n    [FieldAlias("productId")]\n    public int ProductId { get; set; }\n    \n    [FieldAlias("name")]\n    public string Name { get; set; }\n    \n    [FieldAlias("price")]\n    public decimal Price { get; set; }\n}