using System;\nusing System.Threading;\n\npublic class SimpleConsoleGame\n{\n    static void Main()\n    {\n        int width = 20;\n        int height = 10;\n        char[,] screenBuffer = new char[width, height];\n        Random rnd = new Random();\n        \n        while (true)\n        {\n            // Update game state\n            for (int x = 0; x < width; x++)\n            {\n                for (int y = 0; y < height; y++)\n                {\n                    screenBuffer[x, y] = (rnd.Next(0, 2) == 0) ? ' ' : '#';\n                }\n            }\n\n            // Render game state\n            Console.SetCursorPosition(0, 0);\n            for (int y = 0; y < height; y++)\n            {\n                for (int x = 0; x < width; x++)\n                {\n                    Console.Write(screenBuffer[x, y]);\n                }\n                Console.WriteLine();\n            }\n\n            // Control the frame rate\n            Thread.Sleep(1000 / 25); // 25 frames per second\n        }\n    }\n}