private void SendMailToAdminToApprove(string email_, string name_)\n{\n    MailMessage msg = new MailMessage();\n    msg.From = new MailAddress("address@domain.com", "Person's Name");\n    msg.To.Add(new MailAddress("a@gmail.com", "Adam"));\n    msg.Subject = "Message Subject";\n    msg.Body = "Mail body content";\n    msg.IsBodyHtml = true;\n    msg.Priority = MailPriority.High;\n\n    // Define the SMTP client settings\n    SmtpClient client = new SmtpClient("smtp.your-email-provider.com", 587); // Replace with your SMTP host and port\n    client.Credentials = new System.Net.NetworkCredential("your-email@domain.com", "your-email-password"); // Replace with your email and password\n    client.EnableSsl = true; // Enable SSL if required by your email provider\n\n    try\n    {\n        client.Send(msg);\n    }\n    catch (Exception ex)\n    {\n        Console.WriteLine("Error sending email: " + ex.Message);\n    }\n}