public static class MyExtensions\n{\n    public static void MyExtensionMethod(this MyObject obj)\n    {\n        if (obj == null)\n        {\n            Console.WriteLine("The object is null");\n            return;\n        }\n        \n        // Extension method logic here\n        Console.WriteLine("The object is not null");\n    }\n}\n\npublic class MyObject\n{\n}\n\nclass Program\n{\n    static void Main()\n    {\n        MyObject myObject = null;\n        myObject.MyExtensionMethod(); // No null reference exception here\n    }\n}