public class Person\n   {\n       private int _id;\n       private string _firstName;\n\n       public virtual int Id => _id;\n       public virtual string FirstName => _firstName;\n\n       protected Person() { }\n\n       public Person(string firstName)\n       {\n           _firstName = firstName;\n       }\n   }\n\n   // NHibernate mapping (using Fluent NHibernate for example)\n   public class PersonMap : ClassMap<Person>\n   {\n       public PersonMap()\n       {\n           Id(x => x.Id).Column("_id");\n           Map(x => x.FirstName).Column("_firstName");\n       }\n   }