public class SynchronizedProperty<T>\n{\n    private readonly object locker = new object();\n    private T value;\n\n    public SynchronizedProperty(T initialValue = default(T))\n    {\n        value = initialValue;\n    }\n\n    public T Value\n    {\n        get\n        {\n            lock (locker)\n            {\n                return value;\n            }\n        }\n        set\n        {\n            lock (locker)\n            {\n                this.value = value;\n            }\n        }\n    }\n}