protected void UploadButton_Click(object sender, EventArgs e)\n{\n    if (FileUploadControl.HasFile)\n    {\n        try\n        {\n            string uploadFolderPath = Server.MapPath("~/Uploads/"); // Define your upload folder path\n            string fileName = Path.GetFileName(FileUploadControl.FileName);\n            string filePath = Path.Combine(uploadFolderPath, fileName);\n\n            // Ensure the file is uploaded only to the specified folder\n            if (filePath.StartsWith(uploadFolderPath, StringComparison.OrdinalIgnoreCase))\n            {\n                FileUploadControl.SaveAs(filePath);\n                UploadStatusLabel.Text = "Upload status: File uploaded successfully!";\n            }\n            else\n            {\n                UploadStatusLabel.Text = "Upload status: Invalid file path.";\n            }\n        }\n        catch (Exception ex)\n        {\n            UploadStatusLabel.Text = "Upload status: The file could not be uploaded. The following error occured: " + ex.Message;\n        }\n    }\n    else\n    {\n        UploadStatusLabel.Text = "Upload status: No file selected.";\n    }\n}