using System;\nusing System.Data;\nusing System.Data.SqlClient;\nusing System.Xml;\n\nclass Program\n{\n    static void Main()\n    {\n        string connectionString = "your_connection_string";\n        string xmlFilePath = "path_to_your_large_xml_file.xml";\n\n        using (SqlConnection connection = new SqlConnection(connectionString))\n        {\n            connection.Open();\n\n            using (XmlReader reader = XmlReader.Create(xmlFilePath))\n            {\n                while (reader.ReadToFollowing("Item"))\n                {\n                    string itemId = reader.GetAttribute("id");\n\n                    if (reader.ReadToDescendant("Link"))\n                    {\n                        do\n                        {\n                            string link = reader.ReadElementContentAsString();\n\n                            using (SqlCommand cmd = new SqlCommand("INSERT INTO XmlData (Item, Link) VALUES (@Item, @Link)", connection))\n                            {\n                                cmd.Parameters.AddWithValue("@Item", itemId);\n                                cmd.Parameters.AddWithValue("@Link", link);\n                                cmd.ExecuteNonQuery();\n                            }\n                        } while (reader.ReadToNextSibling("Link"));\n                    }\n                }\n            }\n        }\n    }\n}