class Program\n{\n    static void Main(string[] args)\n    {\n        ServiceHost host = new ServiceHost(typeof(MyService), new Uri("net.tcp://localhost:8080/MyService"));\n        host.AddServiceEndpoint(typeof(IMyService), new NetTcpBinding(), "");\n        host.Open();\n\n        Console.WriteLine("Service is running. Press Enter to exit...");\n        Console.ReadLine();\n\n        host.Close();\n    }\n}