// GET: /Stationery/ShowHtml/5\n[HttpGet]\n[Route("Stationery/ShowHtml/{id}")]\npublic ContentResult ShowHtml(int id, bool asHtml)\n{\n    if (!asHtml)\n        return RedirectToAction("Show", new { id = id });\n\n    var result = Stationery.Load(id);\n    return Content(result.GetHtml());\n}\n\n// GET: /Stationery/ShowXml/5\n[HttpGet]\n[Route("Stationery/ShowXml/{id}")]\npublic XmlResult ShowXml(int id)\n{\n    var result = Stationery.Load(id);\n    return new XmlResult(result);\n}