using System;\nusing System.Security;\nusing System.Security.Permissions;\n\nclass Program\n{\n    static void Main()\n    {\n        try\n        {\n            // Request the FileIOPermission to restrict file access to read-only\n            FileIOPermission filePermission = new FileIOPermission(FileIOPermissionAccess.Read, "C:\\example.txt");\n            filePermission.Demand();\n\n            Console.WriteLine("Permission granted to read the file.");\n        }\n        catch (SecurityException se)\n        {\n            Console.WriteLine("Security Exception: " + se.Message);\n        }\n    }\n}