private void Form_Validating(object sender, CancelEventArgs e)\n{\n    // Assuming bindingSource is your BindingSource object\n    var dataItem = bindingSource.Current as YourDataType;\n\n    if (dataItem != null)\n    {\n        // Validate fields directly from the data item\n        if (string.IsNullOrEmpty(dataItem.FieldOnTab2))\n        {\n            // Handle validation failure\n            e.Cancel = true;\n            MessageBox.Show("Field on Tab 2 is required.", "Validation Error", MessageBoxButtons.OK, MessageBoxIcon.Error);\n        }\n    }\n    else\n    {\n        // Handle case where bindingSource.Current is null\n        e.Cancel = true;\n        MessageBox.Show("Data source is not properly initialized.", "Validation Error", MessageBoxButtons.OK, MessageBoxIcon.Error);\n    }\n}