using System.Windows;\nusing System.Windows.Controls;\nusing System.Windows.Data;\n\npublic partial class YourUserControl : UserControl\n{\n    public YourViewModel ViewModel { get; set; }\n\n    public YourUserControl()\n    {\n        InitializeComponent();\n        ViewModel = new YourViewModel();\n        DataContext = ViewModel;\n\n        Validation.AddErrorHandler(txtSomething, ValidationErrorHandler);\n    }\n\n    private void ValidationErrorHandler(object sender, ValidationErrorEventArgs e)\n    {\n        if (e.Action == ValidationErrorEventAction.Added)\n        {\n            ViewModel.IsFormValid = false;\n        }\n        else if (e.Action == ValidationErrorEventAction.Removed)\n        {\n            ViewModel.IsFormValid = true;\n        }\n    }\n}