using System.Windows.Forms;\nusing System.Windows.Forms.Integration;\n\npublic partial class YourForm : Form\n{\n    private YourUserControl wpfUserControl;\n    private Button saveButton;\n\n    public YourForm()\n    {\n        InitializeComponent();\n        ElementHost elementHost = new ElementHost();\n        wpfUserControl = new YourUserControl();\n        elementHost.Child = wpfUserControl;\n        Controls.Add(elementHost);\n        \n        saveButton = new Button();\n        saveButton.Text = "Save";\n        saveButton.DataBindings.Add("Enabled", wpfUserControl.ViewModel, "IsFormValid", true, DataSourceUpdateMode.OnPropertyChanged);\n        Controls.Add(saveButton);\n    }\n}