public class MyClass\n{\n    private static MyClass _instance;\n    private static readonly object _lock = new object();\n    public static event EventHandler InitializationFailed;\n\n    public bool IsInitializedSuccessfully { get; private set; }\n\n    private MyClass()\n    {\n        try\n        {\n            // Perform your initialization here\n            // E.g., reading from a file\n            ReadDefaultData();\n            IsInitializedSuccessfully = true;\n        }\n        catch (Exception)\n        {\n            IsInitializedSuccessfully = false;\n            InitializationFailed?.Invoke(this, EventArgs.Empty);\n        }\n    }\n\n    public static MyClass GetInstance()\n    {\n        if (_instance == null)\n        {\n            lock (_lock)\n            {\n                if (_instance == null)\n                {\n                    _instance = new MyClass();\n                }\n            }\n        }\n        return _instance;\n    }\n\n    private void ReadDefaultData()\n    {\n        // Your code to read default data from file\n        // Throw an exception if reading fails\n    }\n}