public class CustomLocation\n{\n    public string ID { get; set; }\n    public string Name { get; set; }\n\n    // Override ToString() if needed\n    public override string ToString()\n    {\n        return Name;\n    }\n}\n\npublic IList<CustomLocation> GetMyLocations()\n{\n    // Return a list of CustomLocation objects\n    return new List<CustomLocation>\n    {\n        new CustomLocation { ID = "1", Name = "Location1" },\n        new CustomLocation { ID = "2", Name = "Location2" }\n    };\n}\n\nvoid SetupDataGridView(DataGridView dataGridView)\n{\n    DataGridViewComboBoxColumn location = new DataGridViewComboBoxColumn()\n    {\n        Name = "Location",\n        DataSource = new BindingSource(GetMyLocations(), null),\n        DisplayMember = "Name",\n        ValueMember = "" // Use the entire object\n    };\n\n    dataGridView.Columns.Add(location);\n}\n\nvoid RetrieveSelectedLocation(DataGridViewRow row)\n{\n    CustomLocation selectedLocation = row.Cells["Location"].Value as CustomLocation;\n    if (selectedLocation != null)\n    {\n        // Now you can access the properties of the selectedLocation object\n        string id = selectedLocation.ID;\n        string name = selectedLocation.Name;\n    }\n}