using System;\nusing System.Drawing;\n\nclass Program\n{\n    static void Main()\n    {\n        string inputPath = "path_to_your_image.jpg";\n        string outputPath = "path_to_save_modified_image.jpg";\n        \n        // Load the image\n        using (Bitmap bitmap = new Bitmap(inputPath))\n        {\n            // Modify the resolution\n            bitmap.SetResolution(300.0f, 300.0f); // Set to desired resolution (e.g., 300 DPI)\n            \n            // Save the image with the new resolution\n            bitmap.Save(outputPath, System.Drawing.Imaging.ImageFormat.Jpeg);\n        }\n    }\n}