using System;\nusing System.ServiceModel;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        ChannelFactory<IPhoneService> factory = new ChannelFactory<IPhoneService>(\n            new NetNamedPipeBinding(),\n            new EndpointAddress("net.pipe://localhost/PhoneService")\n        );\n\n        IPhoneService proxy = factory.CreateChannel();\n        proxy.CallNumber("123-456-7890");\n\n        ((IClientChannel)proxy).Close();\n        factory.Close();\n    }\n}