protected void Page_Load(object sender, EventArgs e)\n{\n    if (!IsPostBack)\n    {\n        // Initialize your object\n        MyObject obj = new MyObject();\n        // Generate a unique key for the session\n        string uniqueKey = Guid.NewGuid().ToString();\n        // Store the unique key in ViewState\n        ViewState["UniqueKey"] = uniqueKey;\n        // Store the object in Session using the unique key\n        Session[uniqueKey] = obj;\n    }\n    else\n    {\n        // Retrieve the unique key from ViewState\n        string uniqueKey = ViewState["UniqueKey"].ToString();\n        // Retrieve the object from Session using the unique key\n        MyObject obj = (MyObject)Session[uniqueKey];\n    }\n}