using HtmlAgilityPack;\n   using System;\n\n   class Program\n   {\n       static void Main()\n       {\n           string htmlSnippet = "<b>die Bundesliga Mannschaften</b>";\n\n           HtmlDocument document = new HtmlDocument();\n           document.LoadHtml(htmlSnippet);\n\n           StandardizeHtml(document);\n\n           string standardizedHtml = document.DocumentNode.OuterHtml;\n           Console.WriteLine(standardizedHtml);\n       }\n\n       static void StandardizeHtml(HtmlDocument document)\n       {\n           foreach (var node in document.DocumentNode.SelectNodes("//b"))\n           {\n               var spanNode = HtmlNode.CreateNode("<span style='font-weight:bold'></span>");\n               spanNode.InnerHtml = node.InnerHtml;\n               node.ParentNode.ReplaceChild(spanNode, node);\n           }\n       }\n   }