using System;\nusing System.Collections.Concurrent;\nusing System.Collections.Generic;\n\npublic static class OnlineUsers\n{\n    private static ConcurrentDictionary<string, DateTime> users = new ConcurrentDictionary<string, DateTime>();\n\n    public static void AddUser(string userId)\n    {\n        users[userId] = DateTime.Now;\n    }\n\n    public static void RemoveUser(string userId)\n    {\n        users.TryRemove(userId, out _);\n    }\n\n    public static List<string> GetOnlineUsers()\n    {\n        return new List<string>(users.Keys);\n    }\n\n    public static void CleanUp()\n    {\n        DateTime now = DateTime.Now;\n        foreach (var user in users)\n        {\n            if ((now - user.Value).TotalMinutes > 20) // 20 minutes session timeout\n            {\n                RemoveUser(user.Key);\n            }\n        }\n    }\n}