using System;\nusing System.Xml;\n\nclass Program\n{\n    static void Main()\n    {\n        string xhtml = @"<html><body><div class='myClass'>Content1</div><div class='myClass'>Content2</div><div class='otherClass'>Content3</div></body></html>";\n        \n        XmlDocument doc = new XmlDocument();\n        doc.LoadXml(xhtml);\n\n        XmlNodeList nodes = doc.SelectNodes("//*[contains(concat(' ', normalize-space(@class), ' '), ' myClass ')]");\n\n        foreach (XmlNode node in nodes)\n        {\n            Console.WriteLine(node.OuterXml);\n        }\n    }\n}