protected void Application_BeginRequest(Object sender, EventArgs e)\n{\n    HttpContext.Current.Response.Cache.SetExpires(DateTime.UtcNow.AddSeconds(60));\n    HttpContext.Current.Response.Cache.SetCacheability(HttpCacheability.Public);\n    HttpContext.Current.Response.Cache.SetMaxAge(new TimeSpan(0, 0, 60));\n    HttpContext.Current.Response.Cache.AppendCacheExtension("must-revalidate, proxy-revalidate");\n}