using System;\nusing System.Diagnostics;\n\npublic class Benchmark\n{\n    public static void Profile(string description, int iterations, Action func)\n    {\n        // JIT warm-up\n        func();\n        // Clean up and force GC\n        GC.Collect();\n        GC.WaitForPendingFinalizers();\n        GC.Collect();\n\n        var watch = new Stopwatch();\n        long totalTicks = 0;\n\n        for (int i = 0; i < iterations; i++)\n        {\n            watch.Restart();\n            func();\n            watch.Stop();\n            totalTicks += watch.ElapsedTicks;\n        }\n\n        double averageMilliseconds = (totalTicks * 1000.0) / Stopwatch.Frequency / iterations;\n        Console.WriteLine($"{description} - Average Time Elapsed: {averageMilliseconds:F4} ms over {iterations} iterations");\n    }\n\n    public static void Main()\n    {\n        Profile("Test", 1000, () =>\n        {\n            // Code to benchmark\n        });\n    }\n}