protected void Application_BeginRequest(Object sender, EventArgs e)\n   {\n       Uri uri = HttpContext.Current.Request.Url;\n       if (uri.Scheme == Uri.UriSchemeHttps && uri.Host == "example.com")\n       {\n           UriBuilder redirectUri = new UriBuilder(uri)\n           {\n               Host = "www.example.com"\n           };\n           HttpContext.Current.Response.Redirect(redirectUri.ToString(), true);\n       }\n   }