using System;\nusing System.Data;\nusing System.Windows.Forms;\n\npublic partial class MainForm : Form\n{\n    private DataTable dataTable;\n    private int totalCount;\n    private int currentPos;\n\n    public MainForm()\n    {\n        InitializeComponent();\n        LoadData();\n        UpdateButtons();\n    }\n\n    private void LoadData()\n    {\n        // Assuming you have a method to load your data into the dataTable\n        dataTable = GetDataFromSomeSource();\n        totalCount = dataTable.Rows.Count;\n        currentPos = 0;\n\n        if (totalCount > 0)\n        {\n            DisplayData(currentPos);\n        }\n    }\n\n    private void DisplayData(int position)\n    {\n        // Update your form with data from the dataTable at the given position\n        DataRow row = dataTable.Rows[position];\n        // Example: textBoxName.Text = row["Name"].ToString();\n    }\n\n    private void UpdateButtons()\n    {\n        btnPrevious.Enabled = currentPos > 0;\n        btnNext.Enabled = currentPos < totalCount - 1;\n    }\n\n    private void btnNext_Click(object sender, EventArgs e)\n    {\n        if (currentPos < totalCount - 1)\n        {\n            currentPos++;\n            DisplayData(currentPos);\n            UpdateButtons();\n        }\n    }\n\n    private void btnPrevious_Click(object sender, EventArgs e)\n    {\n        if (currentPos > 0)\n        {\n            currentPos--;\n            DisplayData(currentPos);\n            UpdateButtons();\n        }\n    }\n\n    private DataTable GetDataFromSomeSource()\n    {\n        // Implement your data fetching logic here\n        DataTable dt = new DataTable();\n        // Example: Fill the DataTable with data\n        return dt;\n    }\n}