class Client\n{\n    static void Main(string[] args)\n    {\n        ChannelFactory<IMyService> factory = new ChannelFactory<IMyService>(new NetTcpBinding(), new EndpointAddress("net.tcp://localhost:8080/MyService"));\n        IMyService proxy = factory.CreateChannel();\n\n        string result = proxy.GetData(42);\n        Console.WriteLine(result);\n\n        ((IClientChannel)proxy).Close();\n        factory.Close();\n    }\n}