using System;\nusing System.Collections.Generic;\nusing System.Linq;\n\npublic class Program\n{\n    public static void Main()\n    {\n        List<int> intList = new List<int>(new int[] {1, 2, 3, 3, 2, 1});\n\n        // Sum of the first three elements (index range 0-2)\n        int sumFirstThree = intList.Take(3).Sum();\n\n        // Sum of the last three elements (index range 3-5)\n        int sumLastThree = intList.Skip(3).Take(3).Sum();\n\n        Console.WriteLine("Sum of first three elements: " + sumFirstThree);\n        Console.WriteLine("Sum of last three elements: " + sumLastThree);\n    }\n}