using System;\nusing System.Data;\nusing System.Data.Odbc;\n\npublic class FieldTypeRetriever\n{\n    public static Dictionary<string, Type> GetFieldTypes(string tableName, OdbcConnection connection)\n    {\n        var fieldTypes = new Dictionary<string, Type>();\n\n        string query = $"SELECT TOP 0 * FROM {tableName}";\n        using (OdbcCommand command = new OdbcCommand(query, connection))\n        {\n            using (OdbcDataAdapter adapter = new OdbcDataAdapter(command))\n            {\n                DataTable schemaTable = new DataTable();\n                adapter.Fill(schemaTable);\n\n                foreach (DataColumn column in schemaTable.Columns)\n                {\n                    fieldTypes[column.ColumnName] = column.DataType;\n                }\n            }\n        }\n\n        return fieldTypes;\n    }\n\n    public static void Main()\n    {\n        string connectionString = "Your ODBC Connection String Here";\n        using (OdbcConnection connection = new OdbcConnection(connectionString))\n        {\n            connection.Open();\n            var fieldTypes = GetFieldTypes("YourTableName", connection);\n\n            foreach (var fieldType in fieldTypes)\n            {\n                Console.WriteLine($"Field: {fieldType.Key}, Type: {fieldType.Value}");\n            }\n        }\n    }\n}