using System;\n   using System.IO;\n   using DDay.iCal;\n\n   class Program\n   {\n       static void Main()\n       {\n           string calendarData = File.ReadAllText("path/to/your/calendar.ics");\n           IICalendarCollection calendars = iCalendar.LoadFromStream(new StringReader(calendarData));\n\n           foreach (var calendar in calendars)\n           {\n               foreach (var evt in calendar.Events)\n               {\n                   Console.WriteLine("Event Summary: " + evt.Summary);\n                   Console.WriteLine("Start: " + evt.Start);\n                   Console.WriteLine("End: " + evt.End);\n                   Console.WriteLine("Location: " + evt.Location);\n               }\n           }\n       }\n   }