using System;\nusing System.Xml;\nusing System.Xml.Schema;\nusing System.Xml.XPath;\n\nclass Program\n{\n    static void Main()\n    {\n        string xsdPath = "path/to/your/schema.xsd";\n        string xmlPath = "path/to/your/document.xml";\n        string xpathExpression = "/RootData/FirstChild/FirstGrandChild";\n\n        XmlSchemaSet schemas = new XmlSchemaSet();\n        schemas.Add(null, xsdPath);\n\n        XmlDocument xmlDocument = new XmlDocument();\n        xmlDocument.Schemas = schemas;\n        xmlDocument.Load(xmlPath);\n\n        bool isValid = true;\n        xmlDocument.Validate((sender, e) =>\n        {\n            Console.WriteLine(e.Message);\n            isValid = false;\n        });\n\n        if (isValid)\n        {\n            XmlNamespaceManager nsmgr = new XmlNamespaceManager(xmlDocument.NameTable);\n            // Add any namespaces if needed\n            // nsmgr.AddNamespace("prefix", "namespaceURI");\n\n            XmlNodeList nodes = xmlDocument.SelectNodes(xpathExpression, nsmgr);\n            if (nodes != null && nodes.Count > 0)\n            {\n                Console.WriteLine("XPath is potentially valid and found {0} nodes.", nodes.Count);\n            }\n            else\n            {\n                Console.WriteLine("XPath is potentially valid but found no nodes.");\n            }\n        }\n        else\n        {\n            Console.WriteLine("XML Document is not valid against the XSD schema.");\n        }\n    }\n}