static int ReadKey()\n{\n    while (true)\n    {\n        ConsoleKeyInfo choice = Console.ReadKey(true); // 'true' to not display the key press in the console\n        if (char.IsDigit(choice.KeyChar))\n        {\n            int result = choice.KeyChar - '0'; // Converts char digit to its integer value\n            return result;\n        }\n        Console.WriteLine("\nSorry, you need to input a number.");\n    }\n}