using System;\nusing System.ServiceModel;\nusing System.ServiceModel.Description;\n\nclass Program\n{\n    static void Main()\n    {\n        Uri baseAddress = new Uri("http://localhost:8000/ImageService");\n        using (ServiceHost host = new ServiceHost(typeof(ImageService), baseAddress))\n        {\n            ServiceEndpoint endpoint = host.AddServiceEndpoint(typeof(IImageService), new WebHttpBinding(), "");\n            endpoint.Behaviors.Add(new WebHttpBehavior());\n\n            host.Open();\n            Console.WriteLine("The service is ready at {0}", baseAddress);\n            Console.WriteLine("Press <Enter> to stop the service.");\n            Console.ReadLine();\n            host.Close();\n        }\n    }\n}