public class Configuration : IConfigurationSource\n{\n    public void Configure()\n    {\n        ResourceSpace.Has.ResourcesOfType<List<YourEntityClass>>()\n            .AtUri("/entities")\n            .HandledBy<YourEntityHandler>()\n            .AsJsonDataContract();\n\n        ResourceSpace.Has.ResourcesOfType<YourEntityClass>()\n            .AtUri("/entities/{id}")\n            .HandledBy<YourEntityHandler>()\n            .AsJsonDataContract();\n    }\n}