using System;\n   using System.IO;\n   using DDay.iCal;\n\n   class Program\n   {\n       static void Main()\n       {\n           string vCardData = File.ReadAllText("path/to/your/contact.vcf");\n           IICalendarCollection vCards = iCalendar.LoadFromStream(new StringReader(vCardData));\n\n           foreach (var vCard in vCards)\n           {\n               foreach (var contact in vCard.Contacts)\n               {\n                   Console.WriteLine("Full Name: " + contact.Name.FullName);\n                   Console.WriteLine("Organization: " + contact.Organization);\n                   Console.WriteLine("Title: " + contact.Title);\n                   Console.WriteLine("Email: " + contact.EmailAddresses[0].Address);\n               }\n           }\n       }\n   }