using System;\nusing System.Data;\nusing System.Data.OracleClient; // Use Oracle.ManagedDataAccess.Client for newer versions\n\npublic void ExecuteLargeTransaction()\n{\n    string connectionString = "your_connection_string_here";\n    \n    using (OracleConnection connection = new OracleConnection(connectionString))\n    {\n        connection.Open();\n        \n        OracleCommand setRollbackSegmentCommand = new OracleCommand(\n            "SET TRANSACTION USE ROLLBACK SEGMENT MY_ROLLBACK_SEGMENT", \n            connection);\n        setRollbackSegmentCommand.ExecuteNonQuery();\n\n        OracleTransaction transaction = connection.BeginTransaction();\n        \n        try\n        {\n            OracleCommand command = connection.CreateCommand();\n            command.Transaction = transaction;\n            \n            // Your transaction commands go here\n            command.CommandText = "INSERT INTO your_table (column1) VALUES ('value1')";\n            command.ExecuteNonQuery();\n\n            // Commit the transaction\n            transaction.Commit();\n        }\n        catch (Exception ex)\n        {\n            // Rollback the transaction in case of an error\n            transaction.Rollback();\n            Console.WriteLine("Transaction rolled back due to an error: " + ex.Message);\n        }\n    }\n}