using System;\nusing System.Diagnostics;\n\npublic class Program\n{\n    public static void OpenUrl(string url)\n    {\n        try\n        {\n            // Ensure the URL is valid\n            Uri uriResult;\n            bool isValidUrl = Uri.TryCreate(url, UriKind.Absolute, out uriResult)\n                              && (uriResult.Scheme == Uri.UriSchemeHttp || uriResult.Scheme == Uri.UriSchemeHttps);\n\n            if (!isValidUrl)\n            {\n                throw new ArgumentException("Invalid URL");\n            }\n\n            // Use ProcessStartInfo to open the URL in the default browser\n            var psi = new ProcessStartInfo\n            {\n                FileName = uriResult.ToString(),\n                UseShellExecute = true // Ensures the URL is opened with the default browser\n            };\n\n            Process.Start(psi);\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine($"An error occurred while trying to open the URL: {ex.Message}");\n        }\n    }\n\n    public static void Main()\n    {\n        string url = "http://www.google.com";\n        OpenUrl(url);\n    }\n}