class Program\n{\n    static void Main()\n    {\n        Database.SetInitializer(new MyDbInitializer());\n\n        using (var context = new MyDbContext())\n        {\n            // Interact with the database here\n            var product = new Product { Name = "Sample Product", Price = 9.99m };\n            context.Products.Add(product);\n            context.SaveChanges();\n        }\n    }\n}