// Example usage of FluentFTP\nusing System;\nusing FluentFTP;\n\nclass Program\n{\n    static void Main()\n    {\n        var client = new FtpClient("ftp.example.com", "username", "password");\n        client.Connect();\n\n        // Upload a file\n        client.UploadFile(@"C:\path\to\file.txt", "/remote/path/file.txt");\n\n        // Download a file\n        client.DownloadFile(@"C:\path\to\file.txt", "/remote/path/file.txt");\n\n        client.Disconnect();\n    }\n}