using System;\nusing System.Runtime.InteropServices;\n\nnamespace MyWin32Dll\n{\n    public class MyClass\n    {\n        [DllExport("AddNumbers", CallingConvention = CallingConvention.StdCall)]\n        public static int AddNumbers(int a, int b)\n        {\n            return a + b;\n        }\n    }\n\n    // This class is used to define the DllExport attribute\n    // Note: You need to add the UnmanagedExports package from NuGet\n    [AttributeUsage(AttributeTargets.Method, Inherited = false)]\n    public sealed class DllExportAttribute : Attribute\n    {\n        public DllExportAttribute(string exportName)\n        {\n            ExportName = exportName;\n            CallingConvention = CallingConvention.StdCall;\n        }\n\n        public string ExportName { get; }\n        public CallingConvention CallingConvention { get; set; }\n    }\n}